function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
	alert('not ready yet');	
    return;
   }
	alert('new value is: ' + newValue);

	try{
	//Type appropriate comment here, and begin script below
		var row01PoType = newValue; // g_form.getValue('row01_po_type');

		// Set the po_type field to the same value
		g_form.setValue('po_type', row01PoType);
	}
	catch(err){
		alert(err.message);
	}


}

