sys_ui_page.client_script:
function getArowIntoLocalStorage() {
		try {
			var ga = new GlideAjax('ReactPOappScriptInclude');
			ga.addParam('sysparm_name', 'getPOdata');
			var sys_id = '5ff198b41b918a506c1036ef034bcb86';
			ga.addParam('sys_id', sys_id);

			ga.getXMLAnswer(function(response) {
				if (!response) {
					localStorage.setItem('poData', 'Invalid response');
				} else {
					// Escape single quotes in the response
					var escapedResponse = response.replace(/'/g, "#!#");

					// Store the escaped response in Local Storage
					localStorage.setItem('poData', escapedResponse);
				}
			});

		} catch (error) {
			localStorage.setItem('poData', 'caught ' + error.message);
		}
}	
